{
  "version": "0.2",
  "phases": {
    "install": {
      "commands": [
        "pwd",
        "ls -ltr",
        "npm install"
      ]
    },
    "build": {
      "commands": [
        "npm run build",
        "npm run cdk synth -- -o dist",
        "pwd",
        "ls -ltr",
        "cd dist",
        "ls -ltr"
      ]
    }
  },
  "artifacts": {
    "base-directory": "dist",
    "files": [
      "*",
      "*.template.json",
      "manifest.json",
      "bin/*",
      "lib/*"
    ]
  }
}

{
  "version": "0.2",
  "phases": {
    "install": {
      "commands": [
        "pwd",
        "ls -ltr",
        "yum -y install unzip",
        "yum -y install java-1.8.0-amazon-corretto",
        "yum -y install java-1.8.0-amazon-corretto-devel",
        "npm install"
      ]
    },
    "build": {
      "commands": [
        "npm run build",
        "cd lambdas"
      ]
    },
    "post_build": {
      "commands": [
        "pwd",
        "ls -ltr",
        "aws --version",
        "java --version",
        "echo Updating function code...",
        "aws lambda update-function-code --function-name DevApplicationDeploymentStack-LambdaD247545B-R5SFLQFMASH6 --zip-file fileb://./lambda-0.1.jar"
      ]
    }
  },
  "artifacts": {
    "base-directory": "lambdas",
    "files": [
      "*/**"
    ]
  }
}
